<?php

namespace AppBundle\Repository;

/**
 * EgsGameRepository
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EgsGameRepository extends BaseRepository
{
    /**
     * @param int $page
     * @param int $limit
     * @param array $sortAndOrders
     * @param array $filters
     *
     * @return array
     */
    public function getList($page = 1, $limit = 0, $sortAndOrders = array(), $filters = array())
    {
        $year = empty($filters['year']) ? null : $filters['year'];
        $month = empty($filters['month']) ? null : $filters['month'];
        $order = 'ASC';
        $em = $this->getEntityManager();
        if (empty($year) && empty($month)) {
            // 全件
            $dql = "SELECT o FROM {$this->_entityName} o ORDER BY o.releaseYmd {$order}";
            $query = $em->createQuery($dql);
        } else {
            if (empty($month)) {
                $year = date('Y', strtotime($year . '-01-01'));
                // 年別
                $needle = $year . '-%';
                $dql = "SELECT o FROM {$this->_entityName} o WHERE o.releaseYmd LIKE :releaseYmd ORDER BY o.releaseYmd {$order}";
                $query = $em->createQuery($dql)->setParameter('releaseYmd', $needle);
            } else {
                $year = date('Y', strtotime($year . '-01-01'));
                $month = date('m', strtotime($year . '-' . $month . '-01'));
                // 年月別
                $needle = $year . '-' . $month . '-%';
                $dql = "SELECT o FROM {$this->_entityName} o WHERE o.releaseYmd LIKE :releaseYmd ORDER BY o.releaseYmd {$order}";
                $query = $em->createQuery($dql)->setParameter('releaseYmd', $needle);
            }
        }
        $records = $query->getArrayResult();
        if (empty($records)) {
            return array();
        }
        $results = array();
        foreach ($records as $record) {
            $temp = $record;
            $releaseYmd = $record['releaseYmd']->format('Y-m-d');
            $temp['releaseYmd'] = $releaseYmd;
            $results[] = $temp;
        }
        return $results;
    }
}
